mapper.select:
select count(id)
from user
where id = #{id}

mapper.update:
update user
pass = md5(#{pass}), name = #{name}, address = #{address}, phone = #{phone}, email = #{email},
where id = #{id}

mapper.delete:
		update user
        set isDel=1
        where id = #{id}

mapper.select:
select id, pass, name, email, address, phone, join_date as joinDate, roll
from user
where id = #{id}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}
